const Skills = () => {
    const [ width, setWidth ] = useState(null);
    const [ skillsBlockHeight, setSkillsBlockHeight ] = useState("200px")
    const [ state, setState ] = useState({
        paused: false
    });

    // This hook listens for resize events, updates the above state with results
    useLayoutEffect(() => {

        // Check to see whether the client is a browser or not
        if (process.browser) {

            // Add the event listener looking for resize. Set the hook.
            window.addEventListener('resize', () => {
                setWidth(window.innerWidth)
            });

            setWidth(window.innerWidth);
        }

        if (width <= 800) {
            setSkillsBlockHeight("300px")
        } else if (width <= 1200 && width >= 801) {
            setSkillsBlockHeight("200px")
        } else {
            setSkillsBlockHeight("100%")
        }

    }, [width])


    return (
        <>
            <section className="section__skills" >

                <Random
                    text={"aewjoaevoaevmoaevmaoeivm"}
                    paused={state.paused}
                    iterations={1}
                    effect="verticalFadeIn"
                    effectChange={2}
                    effectDirection="up"
                />

            </section>

            {/* <ProgressiveImage
                preview="/static/bogomil-mihaylov-small.jpg"
