import { useEffect, useState } from "react";
import { Tween, Timeline } from 'react-gsap';
import { Transition, TransitionGroup } from 'react-transition-group';

const TransitionItem = ({ children, ...props }) => {
    // const [ state, setState ] = useState({
    //     items: [
    //         { text: '0' },
    //     ],
    // })


    return (
        <Transition timeout={2000} {...props} >
            {(state) => (
                <Timeline
                    wrapper={
                        <section className="block--about-wrapper" />
                    }
                    target={
                        <div className="block--about">
                            <p>
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                                Molestiae, rerum, facilis qui fuga quia molestias, laboriosam 
                                id veritatis ea nam voluptas eligendi atque maiores eius 
                                tenetur reprehenderit unde expedita! Repellendus?
                            </p>
                        </div>
                    }
                    paused={true}
                    playState={
                        state === 'entering' ? 
                        Tween.playState.reverse : 
                        state === 'exiting' ? 
                        Tween.playState.play : 
                        null
                    }
                >

                    <Tween 
                        from={{ y: '-900px' }} 
                        to={{ y: '0px' }} 
                        duration={1.7} 
                        ease="Elastic.easeOut" 
                        delay={0} 
                    />
                    <Tween 
                        staggerTo={{ x: '700px' }} 
                        stagger={0.2} 
                        duration={0.7} 
                        ease="Back.easeOut" 
                        position="+=1" 
                    />

                </Timeline>
            )}
        </Transition>
    )
};



const About = () => {

    return (
        <section className="section__about">

            <TransitionGroup>
                <TransitionItem {...props} />
            </TransitionGroup>

        </section>
    )
};


export default About;
