import React from "react";
import { DropTarget } from "react-dnd";
import ItemTypes from "./ItemTypes";
import DraggableBox from "./DraggableBox";
import snapToGrid from "./snapToGrid";
import update from "immutability-helper";



class Container extends React.PureComponent {
    constructor() {
        super(...arguments);
        this.state = {
            boxes: {
                a: { top: 20, left: 80, title: "Lakeside Herbal", image: "/static/project/lakeside-logo-dark(1080x1080).png" },
                b: { top: 180, left: 20, title: "Magic cards", image: "/static/project/magical-screenshot.png" }
            }
        };
    }

    render() {
        const { connectDropTarget } = this.props;
        const { boxes } = this.state;

        return connectDropTarget(
            <main className="main__drag-layer-container">
                {Object.keys(boxes).map(key => this.renderBox(boxes[key], key))}
            </main>
        );
    }
    moveBox(id, left, top) {
        this.setState(
            update(this.state, {
                boxes: {
                    [id]: {
                        $merge: { left, top }
                    }
                }
            })
        );
        console.log("Move box just fired inside of container")
    }
    renderBox(item, key) {
        return <DraggableBox key={key} id={key} {...item} />;
    }
}

export default DropTarget(
    ItemTypes.BOX,
    {
        drop(props, monitor, component) {
            if (!component) {
                return;
            }
            const delta = monitor.getDifferenceFromInitialOffset();
            const item = monitor.getItem();
            let left = Math.round(item.left + delta.x);
            let top = Math.round(item.top + delta.y);
            if (props.snapToGrid) {
                [left, top] = snapToGrid(left, top);
            }
            component.moveBox(item.id, left, top);
        }
    },
    connect => ({
        connectDropTarget: connect.dropTarget()
    })
)(Container);
