import { useEffect, useState } from "react";
import { Tween, Timeline } from 'react-gsap';
import { Transition, TransitionGroup } from 'react-transition-group';

const TransitionItem = ({ children, onClick, ...props }) => (

    <Transition timeout={2000} {...props}>
        {(state) => (
            <Timeline
                wrapper={
                    <div />
                }
                target={
                    <div className="block--about">
                        <p>
                            Lorem ipsum dolor sit amet consectetur adipisicing elit. Molestiae, rerum, facilis qui fuga quia molestias, laboriosam id veritatis ea nam voluptas eligendi atque maiores eius tenetur reprehenderit unde expedita! Repellendus?
                        </p>
                    </div>
                }
                paused={true}
                playState={state === 'entering' ? Tween.playState.reverse : state === 'exiting' ? Tween.playState.play : null}
            >
                <Tween
                    from={{
                        height: '0px'
                    }}
                    duration={1}
                    ease="Back.easeInOut"
                />
                <Tween
                    to={{
                        height: 'auto'
                    }}
                    duration={0}
                />
                <Tween
                    from={{
                        xPercent: 0,
                        opacity: 1,
                    }}
                    to={{
                        xPercent: 100,
                        opacity: 0,
                    }}
                    duration={1}
                    ease="Back.easeInOut"
                />
            </Timeline>
        )}
    </Transition>

);



const About = () => {
    const [ style, setStyle ] = useState({
        display: "none",
        height: "none"
    });

    useEffect(() => {
        const setTheStyle = () => {
            setStyle({
                display: "flex",
                height: "auto"
            });
        };

        setTimeout(() => setTheStyle(), 1560);

    }, [])


    return (
        <>
            <TransitionGroup component={null}>
                <TransitionItem>
                    <p>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Molestiae, rerum, facilis qui fuga quia molestias, laboriosam id veritatis ea nam voluptas eligendi atque maiores eius tenetur reprehenderit unde expedita! Repellendus?
                    </p>
                </TransitionItem>
            </TransitionGroup>
            <section className="section__about">

                <div className="block--about" style={style}>
                    <p>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Molestiae, rerum, facilis qui fuga quia molestias, laboriosam id veritatis ea nam voluptas eligendi atque maiores eius tenetur reprehenderit unde expedita! Repellendus?
                    </p>
                </div>

            </section>
        </>
    )
};


export default About;
