import React, { useCallback, useEffect, useRef } from "react";
import * as THREE from 'three';
import {
    Canvas,
    extend as extendThree,
    useFrame,
    useLoader,
    useResource,
    useThree
} from "react-three-fiber";

// Import and register postprocessing classes as three-native-elements for react-three-fiber
// They'll be available as native elements <effectComposer /> from then on ...
import { EffectComposer } from './resources/postprocessing/EffectComposer';
import { RenderPass } from './resources/postprocessing/RenderPass';
import { GlitchPass } from './resources/postprocessing/GlitchPass';
extendThree({ EffectComposer, RenderPass, GlitchPass });

// import TextLoop from "../TextLoop";





// // Creates a fullscreen colored plane
// const Background = ({ color }) => {
//     const { viewport } = useThree();

//     return (
//         <mesh scale={[viewport.width, viewport.height, 1]}>
//             <planeGeometry attach="geometry" args={[1, 1]} />
//             <meshPhysicalMaterial attach="material" color={color}  depthTest={false} />
//         </mesh>
//     )
// }






// The canvas houses this function, the scene. Scene is where the elements come together.
const Scene = () => {
    const ref = useRef();
    const [geometryRef, geometry] = useResource();
    const [materialRef, material] = useResource();

    useFrame(() => {
        return ref.current.rotation.x = ref.current.rotation.y += 0.01
    });

    console.log('materialRef --> ', materialRef);
    console.log('material --> ', material);
    console.log('ref --> ', ref);

    return (
        <>
            <group 
                ref={ref}  
            >

                {/* <pointLight
                    color="#ffffff"
                    intensity={1.3} 
                    position={[50, 50, 50]} 
                    distance={50}
                    decay={2}
                />
                <meshLambertMaterial 
                    ref={materialRef}
                    color="#00aa33"
                    transparent={true}
                    opacity={0.8}
                    // map={smokeTexture}
                />  */}

                <mesh 
                    userData={{ test: 'hello' }} 
                    position={[1, 2, 3]} 
                    rotation={[0, 0, 0]}
                    scale={{ x: 2.0, y: 2.0, z: 2.0 }}
                    // radius={100}
                    // onClick={e => console.log('click')}
                    // onPointerOver={e => console.log('hover')}
                    // onPointerOut={e => console.log('unhover')}
                    visible 
                >
                    <dodecahedronGeometry 
                        attach="geometry" 
                        // args={[10000, 10000]} 
                        detail={1}
                    />
                    <meshNormalMaterial 
                        attach="material" 
                    />
                </mesh>
            </group>
        </>
    )
}


// This is the canvas. It's the lowest level element in the three/webGl chain
const Canvasical = () => {


    return (
        <>
            <Canvas
                className="container block__three-container" 
                camera={{ fov: 75, position: [0, 0, 50] }} 
                style={{ background: 'radial-gradient(at 50% 70%, #200f20 40%, #090b1f 80%, #050523 100%)' }}
                // children                      // Either a function child (which receives state) or regular children
                // gl                            // Props that go into the default webGL-renderer
                // camera                        // Props that go into the default camera
                // raycaster                     // Props that go into the default raycaster
                // shadowMap                     // Props that go into gl.shadowMap, can also be set true for PCFsoft
                // vr = false                    // Switches renderer to VR mode, then uses gl.setAnimationLoop
                // gl2 = false                   // Enables webgl2
                // concurrent = false            // Enables React concurrent mode
                // resize = undefined            // Resize config, see react-use-measure's options
                // orthographic = false          // Creates an orthographic camera if true
                // noEvents = false              // Switch off raytracing and event support
                // pixelRatio = undefined        // You could provide window.devicePixelRatio if you like
                // invalidateFrameloop = false   // When true it only renders on changes, when false it's a game loop
                // updateDefaultCamera = true    // Adjusts default camera on size changes
                // onCreated                     // Callback when vdom is ready (you can block first render via promise)
                // onPointerMissed 
            >

                <Scene />

            </Canvas> 

            {/* <TextLoop  /> */}

        </>
    );
}

export default Canvasical;
