import { useState, useEffect } from "react";
import SimpleIcons from 'simple-icons-react-component';
import TextLoop from "react-text-loop";



const Skills = () => {
    const namesOfSkills = [ "Next.js", "React", "Redux", "GraphQL", "Node.js", "PostgreSQL", "Apache", "Docker", "Debian", "GNOME", "Adobe Illustrator", "GIMP", "Sass", "Ripple", "Mapbox" ] 
    const characters = "日アイウエオカキクケコサシスセソタチツテトナニヌネノハヒフヘホマミムメモヤユヨラリルレロワヲンあいうえおかきくけこさしすせそたちつてとなにぬねのはひふへほまみむめもやゆよらりるれろわをん";
    const [ skillsWithCharacters, setSkillsWithCharacters ] = useState();

    useEffect(() => {
        if (!skillsWithCharacters) {
            const finalArray = getArrSkillsWithCharacters(namesOfSkills, characters);
            setSkillsWithCharacters(finalArray);
        }
    }, [])

    const getRandomInt = (min, max) => {
        min = Math.ceil(min);
        max = Math.floor(max);
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    const getNumberOfRandomCharacters = (number, chars) => {
        if (!number) number = 9;
        return chars.substr(getRandomInt(0, chars.length - number), number + 1)
    }

    const getArrSkillsWithCharacters = (skills, chars) => {
        let skillCharArr = [];
        for (let i=0; i<=88; i++) {
            let selectSkill = skills[getRandomInt(0, namesOfSkills.length - 1)];
            let createChars = getNumberOfRandomCharacters(selectSkill.length - 1, chars)
            skillCharArr.push(selectSkill, createChars);
        };
        return skillCharArr;
    }

    return (
        <>
            <section className="section__skills" >

                <main className="main__textlooper">
                    <span className="span__title">I love building with </span>
                    {skillsWithCharacters ? (
                        <TextLoop 
                            interval={[2400, 900]}
                            // interval={textLoopInterval}
                            // children={textLoopChildren}
                            delay={0}
                            mask={false} 
                            fade={true}
                            noWrap={true}
                            springConfig={{ stiffness: 300, damping: 53, precision: 0.01 }}
                            adjustingSpeed={150}
                            className="text-loop"
                        >
                            {skillsWithCharacters.map((entry, index) => (
                                <>
                                    <span key={index + "-" + entry}>{skillsWithCharacters[index]}</span>
                                </>
                            ))}
                        </TextLoop >
                    ) : null}
                </main>


                <div className="block__skill-icons">
                    {namesOfSkills.map((eachSkill, index) => (
                        <SimpleIcons 
                            key={index}
                            name={eachSkill}
                            className="icon--skills"
                        />
                    ))}
                </div>


            </section>
        </>
    );
}


export default Skills;
