import React, {
    useCallback,
    useEffect,
    useRef,
    useState,
    useMemo
} from "react";
import * as THREE from "three";
import {
    Canvas,
    extend as extendThree,
    useFrame,
    useLoader,
    useResource,
    useThree
} from "react-three-fiber";

// Import and register postprocessing classes as three-native-elements for react-three-fiber
// They'll be available as native elements <effectComposer /> from then on ...
import { EffectComposer } from "./resources/postprocessing/EffectComposer";
import { RenderPass } from "./resources/postprocessing/RenderPass";
import { GlitchPass } from "./resources/postprocessing/GlitchPass";
extendThree({ EffectComposer, RenderPass, GlitchPass });

// import TextLoop from "../TextLoop";


// Cast the lighting
const Lighting = () => {
    const { mouse } = useThree();
    const light = useRef();

    useFrame(() => {
        light.current.position.set( mouse.x / 20, -mouse.y / 20, 0 )
    });

    return (
        <>
            <directionalLight
                position={[ -2, 0, 30 ]}  intensity={0.5}  color="#ffffff" 
            />
            <pointLight 
                ref={light} distance={50} intensity={1.5} color="#ffffff" 
            />
        </>
    );
};

// The canvas houses this function, the scene. Scene is where the elements come together.
function SmokePuff({ geometry, material, particleArray }) {
    let smokePuffRef = useRef();
    let tenToHundred = Math.random() * 100; // 10 - 99.99
    let speed = 0.01 + Math.random() / 200; // 0.1 - 0.19
    let factor = 20 + Math.random() * 100;  // ~ 20-30
    let xFactor = -50 + Math.random() * 100;  // -50 - 50
    let yFactor = -50 + Math.random() * 100;  // -50 - 50
    let zFactor = 10 + Math.random() * 10;  //  10 - 20

    const getRandomArbitrary = (min, max) => Math.random() * (max - min) + min;
    let randomFactor = getRandomArbitrary(2, 3);

    useFrame(({ clock }) => {
        tenToHundred += speed; // tenToHundred = tenToHundred + speed, plus it sets a unique new version of t.
        let delta = clock.getDelta();  // Change in clock. Something like 0.0001 - 0.0099
        const cosWave = (Math.cos(tenToHundred) / 3) + 1.2;  // a cosign wave
        let slowAndSteady = ((clock.elapsedTime * (delta + 0.017)) * randomFactor) + xFactor;  // starts at ~0.0001 + xFactor and slowly increases

        smokePuffRef.current.scale.set(cosWave, cosWave, 2);
        smokePuffRef.current.rotation.z = slowAndSteady; 
        smokePuffRef.current.position.set(
            xFactor + Math.cos((tenToHundred/ 30) * factor) + (Math.sin(tenToHundred* 1) * factor) / 10,
            yFactor + Math.sin((tenToHundred/ 20) * factor) + (Math.cos(tenToHundred* 2) * factor) / 10,
            zFactor
            // zFactor + Math.cos((tenToHundred/ 10) * factor) + (Math.sin(tenToHundred* 3) * factor) / 20
        );
    });

    console.log('smokePuffRef --> ', smokePuffRef);

    return <mesh  ref={smokePuffRef} material={material} geometry={geometry}  />;
}

const GenerateSmoke = ({ particleArray }) => {
    const [geometryRef, geometry] = useResource();
    const [materialRef, material] = useResource();
    const url = 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/95637/Smoke-Element.png';
    const texture = useMemo(() => new THREE.TextureLoader().load(url), [url]);

    return (
        <>
            <Lighting />

            <planeGeometry 
                ref={geometryRef} 
                attach="geometry"   
                args={[200, 200]} 
            />
            <meshLambertMaterial 
                ref={materialRef}
                attach="material" 
                color="#ccbbee"
                transparent
            >
                <primitive attach="map" object={texture} />
            </meshLambertMaterial>

            {geometry && new Array(3).fill().map((_, index) => (
                <SmokePuff
                    key={index}
                    material={material}
                    geometry={geometry}
                    particleArray={particleArray}
                />
            ))}
        </>
    );
}


// This is the canvas. It's the lowest level element in the three/webGl chain
const CanvasicalSmoke = () => {
    const particleCount = 3;
    const particleArray = [{ id: 0, x: 0, y: 0, z: 2 }];

    useEffect(() => {
        if (particleArray.length === particleCount) {
            return null;
        } else {
            for (let i = 0; i <= particleCount; i++) {
                particleArray.push({
                    id: particleArray[i].id + 1,
                    x: particleArray[i].x + 1,
                    y: particleArray[i].y + 1,
                    z: 2
                });
            }
        }
    }, [particleArray]);

    return (
        <>
            <Canvas
                className="container block__three-container"
                camera={{
                    fov: 75,
                    aspect: 0.5,
                    near: 1,
                    far: 10000,
                    position: [0, 0, 300]
                }}
            >
                <GenerateSmoke particleArray={particleArray} />
            </Canvas>

            {/* <TextLoop  /> */}
        </>
    );
};

export default CanvasicalSmoke;

