    const [ boolean, setBoolean ] = useState(false)
    const [ style, setStyle ] = useState({
        display: "none"
    });

    useEffect(() => {
        setTimeout(setTheStyle, 2000);
        const setTheStyle = () => {
            setBoolean(true);
        };


        if (boolean === false) {
            setStyle({
                display: "flex"
            });
        }
    }, [])




    return (
        <section className="section__about">

