const Skills = () => {
    const [ textLoopChildren, setTextLoopChildren ] = useState("")

    const characters = "日アイウエオカキクケコサシスセソタチツテトナニヌネノハヒフヘホマミムメモヤユヨラリルレロワヲンあいうえおかきくけこさしすせそたちつてとなにぬねのはひふへほまみむめもやゆよらりるれろわをん";

    useEffect(() => {
        setTextLoopChildren(characters);
    }, [])



    // This hook listens for resize events, updates the above state with results
    // useLayoutEffect(() => {

    //     // Check to see whether the client is a browser or not
    //     if (process.browser) {

    //         // Add the event listener looking for resize. Set the hook.
    //         window.addEventListener('resize', () => {
    //             setWidth(window.innerWidth)
    //         });

    //         setWidth(window.innerWidth);
    //     }

    //     if (width <= 800) {
    //         setSkillsBlockHeight("300px")
    //     } else if (width <= 1200 && width >= 801) {
    //         setSkillsBlockHeight("200px")
    //     } else {
    //         setSkillsBlockHeight("100%")
    //     }

    // }, [width])


    return (
        <>
            <section className="section__skills" >


                <div className="block__skill-icons">
                    <SimpleIcons 
                        name="Next.js" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="React" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Redux" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="GraphQL" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Node.js" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="PostgreSQL" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Apache" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Docker" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Debian" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="GNOME" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Adobe Illustrator" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="GIMP" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Sass" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Ripple" 
                        className="icon--skills"
                    />
                    <SimpleIcons 
                        name="Mapbox" 
                        className="icon--skills"
                    />
                </div>


                {/* <Random
                    text={characters}
                    paused={state.paused}
                    iterations={1}
                    effect="verticalFadeIn"
                    effectChange={2}
                    effectDirection="up"
                /> */}

                <TextLoop 
                    interval={[3000, 1000]} 
                    interval={interval} 
                    children={textLoopChildren}
                    delay={0}
                    mask={true} 
                    fade={false}
                />

            </section>

            {/* <ProgressiveImage
                preview="/static/bogomil-mihaylov-small.jpg"
                src="/static/bogomil-mihaylov-wVTLXWM-Gh0-unsplash.jpg"
                transitionTime={700}
                transitionFunction="ease"
                render={(src, style) => (
                    <section className="section__skills" style={Object.assign(style, { backgroundImage: `url(${src})` })} >
                        {style.filter === "blur(0px)" ? (
                            <SkillsBody   />
                        ) : null}
                    </section>
                )}
            /> */}

        </>
    );
}


export default Skills;
