import Link from 'next/link';
import { useEffect, useHook } from "react";



const Nav = (props) => {
    const { route } = props || "";
    const [ currentNavLocation, setCurrentNavLocation ] = useEffect("");


    useEffect(() => {

        if (props && props.route === "/") {
            setCurrentNavLocation("index")
        } else if (props && props.route === "/about") {
            setCurrentNavLocation("about")
        } else if (props && props.route === "/projects") {
            setCurrentNavLocation("projects")
        };

        // console.log('navLocation --> ', currentNavLocation);
    }, [props.route])



    return (

        <section className={`section__nav route-${currentNavLocation}`}>
            <nav>


                <Link href="/">
                    <a>Home</a>
                </Link>
                <Link href="/about">
                    <a>About</a>
                </Link>

                <Link href="/projects">
                    <a>Projects</a>
                </Link>

            </nav>
        </section>

    )
}

export default Nav
