//             <planeGeometry attach="geometry" args={[1, 1]} />
//             <meshPhysicalMaterial attach="material" color={color}  depthTest={false} />
//         </mesh>
//     )
// }






// The canvas houses this function, the scene. Scene is where the elements come together.
const ObjectMeshTetra = () => {

    const ref = useRef();
    // const [geometryRef, geometry] = useResource();
    // const [materialRef, material] = useResource();

    useFrame(() => {
        return ref.current.rotation.x = ref.current.rotation.y += 0.01
    });

    return (
        <>
            <group 
                ref={ref}
                scale={[ 1,1,1 ]}
                receiveShadow={true}
                visible
            >
                {/* 
                <meshLambertMaterial 
                    ref={materialRef}
                    color="#00aa33"
                    transparent={true}
                    opacity={0.8}
                    // map={smokeTexture}
                />  */}
                {/* <dodecahedronGeometry 
                    attach="geometry" 
                    // args={[10000, 10000]} 
                    detail={1}
                /> */}

                <mesh 
                    userData={{ test: 'hello' }} 
                    position={[ 1, 2, 3 ]} 
                    rotation={[ 0, 0, 0 ]}
                    scale={[ 2.0, 2.0, 2.0 ]}
                    receiveShadow={true}
                    // radius={100}
                    // onClick={e => console.log('click')}
                    // onPointerOver={e => console.log('hover')}
                    // onPointerOut={e => console.log('unhover')}
                    visible 
                >
                    <torusKnotBufferGeometry 
                        attach="geometry" 
                        args={[0.5, 0.15, 150, 32]} 
                    />
                    <meshPhysicalMaterial 
                        attach="material" 
                        color="#0077ff"
                        roughness={1} 
                        clearcoat={1} 
                        clearcoatRoughness={0.2}
                    />
                </mesh>
            </group>
        </>
    )
}

