// import { useEffect, useState } from "react";
import { Tween, Timeline } from 'react-gsap';
import { Transition, TransitionGroup } from 'react-transition-group';

const TransitionItem = ({ children, ...props }) => {
    console.log('props --> ', props)

    return (
        <Transition timeout={2000} {...props}>
            {(state) => (
                <Timeline
                    wrapper={
                        <section className="block--about-wrapper"></section>
                    }
                    target={
                        <div className="block--about">
                            {children}
                        </div>
                    }
                    paused={true}
                    playState={state === 'entering' ? Tween.playState.reverse : state === 'exiting' ? Tween.playState.play : null}
                >
                    <Tween
                        from={{
                            height: '0px'
                        }}
                        opacity={0}
                        duration={1}
                        ease="Back.easeInOut"
                    />
                    <Tween
                        to={{
                            height: 'auto'
                        }}
                        opacity={1}
                        duration={0}
                    />
                    {/* <Tween
                        from={{
                            xPercent: 0,
                            opacity: 1,
                        }}
                        to={{
                            xPercent: 100,
                            opacity: 0,
                        }}
                        duration={1}
                        ease="Back.easeInOut"
                    /> */}
                </Timeline>
            )}
        </Transition>
    )
};



const About = () => {

    return (
        <section className="section__about">

            <TransitionGroup>
                <TransitionItem children={(
                    <p>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                        Molestiae, rerum, facilis qui fuga quia molestias, laboriosam 
                        id veritatis ea nam voluptas eligendi atque maiores eius 
                        tenetur reprehenderit unde expedita! Repellendus?
                    </p>
                )} />
            </TransitionGroup>

        </section>
    )
};


export default About;
