import Link from 'next/link';
import { useEffect } from "react";



const Nav = (props) => {
    const { route } = props;

    let navLocation = "";


    useEffect(() => {

        if (route === "/") {
            navLocation = "index"
        } else if (route === "/about") {
            navLocation = "about"
        } else if (route === "/projects") {
            navLocation = "projects"
        };

        console.log('navLocation --> ', navLocation);
    }, [route])



    return (

        <section className={`section__nav route-${navLocation}`}>
            <nav>


                <Link href="/">
                    <a>Home</a>
                </Link>
                <Link href="/about">
                    <a>About</a>
                </Link>

                <Link href="/projects">
                    <a>Projects</a>
                </Link>

            </nav>
        </section>

    )
}

export default Nav
