const Skills = () => {
    const [ textLoopChildren, setTextLoopChildren ] = useState("");
    const [ textLoopInterval, setTextLoopInterval ] = useState([3000, 1000]);
    const namesOfSkills = [ "Next.js", "React", "Redux", "GraphQL", "Node.js", "PostgreSQL", "Apache", "Docker", "Debian", "GNOME", "Adobe Illustrator", "GIMP", "Sass", "Ripple", "Mapbox" ] 
    const characters = "日アイウエオカキクケコサシスセソタチツテトナニヌネノハヒフヘホマミムメモヤユヨラリルレロワヲンあいうえおかきくけこさしすせそたちつてとなにぬねのはひふへほまみむめもやゆよらりるれろわをん";

    useEffect(() => {
        setTextLoopChildren(characters);
    }, [])



    const randomNumberGen = () => {
        let randomNumber = Math.floor(Math.random() * 100);

    }

    function getRandomInt(min, max) {
        min = Math.ceil(min);
        max = Math.floor(max);
        console.log(Math.floor(Math.random() * (max - min + 1)) + min)
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }


    return (
        <>
            <section className="section__skills" >

                <div className="block__skill-icons">
                    {namesOfSkills.map((eachSkill, index) => (
                        <SimpleIcons 
                            key={index}
                            name={eachSkill}
                            className="icon--skills"
                        />
                    ))}
                </div>

                {getRandomInt(0, 15)}
                <main className="main__textlooper">
