                        mask={true} 
                        fade={true}
                        noWrap={true}
                        springConfig={{ stiffness: 70, damping: 31, precision: 0.01 }}
                        adjustingSpeed={150}
                        className="text-loop"
                    >
                        {namesOfSkills.map((eachSkill, index) => (
                            <React.Fragment key={index}>
                                <span>{eachSkill}</span>
                            </React.Fragment>
                        ))}

                        <span>{textLoopChildren}</span>
