import ProgressiveImage from "react-progressive-image-loading";
import { useLayoutEffect, useState } from "react";

import { Tween } from 'react-gsap';




// This is the tween fade in function
const FadeIn = ({ children, duration, skillsBlockHeight }) => {
    return (
        <Tween
            from={{ css: { opacity: 0, height: "0px", display: "none", backgroundColor: "unset" }}}
            to={{ css: { opacity: 1, height: skillsBlockHeight, display: "flex", backgroundColor: "#33337f"  }}}
            duration={duration}
        >
            {children}
        </Tween>
    )
};


// This is the body of the skills page
const SkillsBody = ({ skillsBlockHeight }) => (
    <section className="block--skills-wrapper" >
        <FadeIn 
            duration={1.8}
            skillsBlockHeight={skillsBlockHeight}
            children={(
                <div className="block--skills">
                    <p>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                        Molestiae, rerum, facilis qui fuga quia molestias, laboriosam 
                        id veritatis ea nam voluptas eligendi atque maiores eius 
                        tenetur reprehenderit unde expedita! Repellendus?
                    </p>
                </div>
            )}
        />
    </section>
)


const Skills = () => {
    const [ width, setWidth ] = useState(null);
    const [ skillsBlockHeight, setSkillsBlockHeight ] = useState("200px")


    // This hook listens for resize events, updates the above state with results
    useLayoutEffect(() => {

        // Check to see whether the client is a browser or not
        if (process.browser) {

            // Add the event listener looking for resize. Set the hook.
            window.addEventListener('resize', () => {
                setWidth(window.innerWidth)
            });

            setWidth(window.innerWidth);
        }

        if (width <= 800) {
            setSkillsBlockHeight("300px")
        } else if (width <= 1200 && width >= 801) {
            setSkillsBlockHeight("200px")
        } else {
            setSkillsBlockHeight("100%")
        }

    }, [width])





    return (
        <>
            <ProgressiveImage
                preview="/static/bogomil-mihaylov-small.jpg"
                src="/static/bogomil-mihaylov-wVTLXWM-Gh0-unsplash.jpg"
                transitionTime={700}
                transitionFunction="ease"
                render={(src, style) => (
                    <section className="section__skills" style={Object.assign(style, { backgroundImage: `url(${src})` })} >
                        {style.filter === "blur(0px)" ? (
                            <SkillsBody  skillsBlockHeight={skillsBlockHeight} />
                        ) : null}
                    </section>
                )}
            />
        </>
    );
}


export default Skills;
