import React, { useCallback, useEffect, useRef, useState, useMemo } from "react";
import * as THREE from 'three';
import {
    Canvas,
    extend as extendThree,
    useFrame,
    useLoader,
    useResource,
    useThree
} from "react-three-fiber";

// Import and register postprocessing classes as three-native-elements for react-three-fiber
// They'll be available as native elements <effectComposer /> from then on ...
import { EffectComposer } from './resources/postprocessing/EffectComposer';
import { RenderPass } from './resources/postprocessing/RenderPass';
import { GlitchPass } from './resources/postprocessing/GlitchPass';
extendThree({ EffectComposer, RenderPass, GlitchPass });

// import TextLoop from "../TextLoop";


// const DEFAULT_LAYER = 0;
// const OCCLUSION_LAYER = 1;


// The canvas houses this function, the scene. Scene is where the elements come together.
const SmokeParticle = () => {
    const ref = useRef();
    const url = 'https://s3-us-west-2.amazonaws.com/s.cdpn.io/95637/Smoke-Element.png';
    const texture = useMemo(() => new THREE.TextureLoader().load(url), [url]);

    let t = Math.random() * 2;
    let speed = Math.random() * 0.003;
    let factor = 20 + Math.random() * 100;
    let xFactor = -50 + Math.random() * 100;
    let yFactor = -50 + Math.random() * 100;
    let zFactor = -30 + Math.random() * 60;

    console.log('THREE --> ', THREE);
    console.log('ref --> ', ref);

    // let position = { x: Math.random() * 500-250, y: Math.random() * 500-250, z: 2 };
    // // let position = (Math.random()*500-250, Math.random()*500-250, 2);
    // let rotation = Math.random() * 360;

    useFrame(() => {
        t += speed;
        const s = Math.cos(t);
        ref.current.rotation.z = s * 5;
        ref.current.position.set(
            xFactor + Math.cos((t / 30) * factor) + (Math.sin(t * 1) * factor) / 10,
            yFactor + Math.sin((t / 20) * factor) + (Math.cos(t * 2) * factor) / 10,
            zFactor + Math.cos((t / 10) * factor) + (Math.sin(t * 3) * factor) / 20
        )
    })

    return (
        <mesh 
            ref={ref}
            position={[ 0, 0, 2 ]} // positions the object above the plane
            visible 
        >
            <meshLambertMaterial 
                attach="material" 
                color="#ccbbee"
                transparent
            >
                <primitive attach="map" object={texture} />
            </ meshLambertMaterial>

            <planeGeometry 
                attach="geometry"   
                args={[300, 300]} 
            />
        </mesh>
    )
}


const GenerateSmoke = () => {

    return (
        <>
            {new Array(80).fill().map((_, index) => <SmokeParticle key={index}  />)}
        </>
    )
}

// Cast the lighting
const Lighting = () => {
    return (
        <directionalLight
            color="#ffffff"
            intensity={0.5}
            position={[ -2, 0, 30 ]} 
        />
    )
}

// This is the canvas. It's the lowest level element in the three/webGl chain
const CanvasicalSmoke = () => {
    return (
        <>
            <Canvas
                className="container block__three-container" 
                camera={{ 
                    fov: 75, 
                    aspect: 0.5,
                    near: 1,
                    far: 10000,
                    position: [ 0, 0, 600 ]
                }} 
            >
                <Lighting />
                <GenerateSmoke  />
            </Canvas> 

            {/* <TextLoop  /> */}
        </>
    );
}

export default CanvasicalSmoke;





