import { useEffect, useLayoutEffect, useState } from "react";
import { Tween } from 'react-gsap';
// import { Transition } from 'react-transition-group';



const FadeIn = ({ children, duration, aboutBlockHeight }) => {


    return (
        <Tween
            from={{ css: { opacity: 0, height: 0, display: "none" }}}
            to={{ css: { opacity: 1, height: aboutBlockHeight, display: "flex" }}}
            duration={duration}
        >
            {children}
        </Tween>
    )
};





const About = () => {
    const [ width, setWidth ] = useState(null);
    const [ aboutBlockHeight, setAboutBlockHeight ] = useState("200px")


    if (process.browser) {
        // useEffect(() => {
        //     setWidth(document.children[0].clientWidth)
        //     if (document.children[0].clientWidth <= 800) {
        //         setAboutBlockHeight("300px")
        //     } else {
        //         setAboutBlockHeight("200px")
        //     }
        // }, [document.children[0].clientWidth] )
        useLayoutEffect(() => {
            setWidth(document.children[0].clientWidth)
            if (document.children[0].clientWidth <= 800) {
                setAboutBlockHeight("300px")
            } else {
                setAboutBlockHeight("200px")
            }
        }, [])

    }



    return (
        <section className="section__about">
            <section className="block--about-wrapper" >
                    <FadeIn 
                        duration={3}
                        aboutBlockHeight={aboutBlockHeight}
                        children={(
                            <div className="block--about">
                                <p>
                                    Lorem ipsum dolor sit amet consectetur adipisicing elit. 
                                    Molestiae, rerum, facilis qui fuga quia molestias, laboriosam 
                                    id veritatis ea nam voluptas eligendi atque maiores eius 
                                    tenetur reprehenderit unde expedita! Repellendus?
                                </p>
                            </div>
                        )}
                    />
            </section>
        </section>
    )
};


export default About;
