import { useRef, useState } from "react";
import * as THREE from 'three';
import {
    Canvas,
    useFrame,
    extend,
    useResource,
    // useLoader,
    // useThree,
} from "react-three-fiber";
import * as resources from './resources';
extend(resources);

import TextLoop from "../TextLoop";






// This component creates a fullscreen colored plane 
