//             <planeGeometry attach="geometry" args={[1, 1]} />
//             <a.meshBasicMaterial attach="material" color={color} depthTest={false} />
//         </mesh>
//     )
// }



// // This component loads an image and projects it onto a plane
// function Image({ url, opacity, scale, ...props }) {
//     const texture = useMemo(() => new THREE.TextureLoader().load(url), [url])
//     const [hovered, setHover] = useState(false)
//     const hover = useCallback(() => setHover(true), [])
//     const unhover = useCallback(() => setHover(false), [])
//     const { factor } = useSpring({ factor: hovered ? 1.1 : 1 })
//     return (
//         <a.mesh {...props} onHover={hover} onUnhover={unhover} scale={factor.interpolate(f => [scale * f, scale * f, 1])}>
//             <planeBufferGeometry attach="geometry" args={[5, 5]} />
//             <a.meshLambertMaterial attach="material" transparent opacity={opacity}>
//                 <primitive attach="map" object={texture} />
//             </a.meshLambertMaterial>
//         </a.mesh>
//     )
// }






const Thing = props => {
    const { vertices } = props;
    const [ geometryRef, geometry ] = useResource();
    const ref = useRef();

    useFrame(() => {
        return ref.current.rotation.x = ref.current.rotation.y += 0.01
    })

    // useFrame(() => (console.log('ref.current --> ', ref.current)))

    return (
        <group 
            ref={ref}
        >
            <line>
                <geometry 
                    attach="geometry"
                    vertices={vertices.map( v => new THREE.Vector3(...v) )}
                    onUpdate={self => (self.verticesNeedUpdate = true)}
                />
                <lineBasicMaterial   attach="material"  color="#ff0000" />
            </line>

            <mesh       // a mesh combines material with geometry
                // onClick={click => console.log('click --> ', click)}
                // onHover={hover => console.log('hover --> ', hover)}
            >
                <octahedronGeometry 
                    attach="geometry"  
                    args={[10000, 10000]}
                    parameters={{ a: 0 }}
                />
                <meshBasicMaterial  
                    attach="material"  
                    color="peachpuff" 
                    opacity={0.5} 
                    transparent={true} 
                />
            </mesh>
        </group>
    )
}


// This component maintains the scene 
const Scene = ({ mouse }) => {
    const { size } = useThree();
    const scrollMax = size.height * 4.5;

    return (
        <>
            {/* <a.spotLight intensity={1.2} color="white" position={mouse.interpolate((x, y) => [x / 100, -y / 100, 6.5])} /> */}
            {/* <Effects factor={top.interpolate([0, 150], [1, 0])} /> */}
            {/* <Background color={top.interpolate([0, 0.25, 0.8, 1], ['#27282F', '#247BA0', '#70C1B3', '#f8f3f1'])} /> */}

            <Thing   vertices={[ [ -1,0,0 ], [ 0,1,0 ], [ 1,0,0 ], [ 0,-1,0 ], [ -1,0,0 ] ]}  />


        </>
    )
}

// Main container
const SmokeContainerReal = () => {
    // const [ mouseState, setMouseState ] = useState({ mouse: [ 0, 0 ] });

    // useFrame(({ clientX: x, clientY: y }) => {
    //     setMouseState({ mouse: [x - window.innerWidth / 2, y - window.innerHeight / 2] });
    // })

    return (
        <div  className="container block__three-container" >
            <Canvas
                style={{ background: 'radial-gradient(at 50% 70%, #200f20 40%, #090b1f 80%, #050523 100%)' }}
                camera={{ fov: 75, position: [0, 0, 60] }} 
                // children                      // Either a function child (which receives state) or regular children
                // gl                            // Props that go into the default webGL-renderer
                // camera                        // Props that go into the default camera
                // raycaster                     // Props that go into the default raycaster
                // shadowMap                     // Props that go into gl.shadowMap, can also be set true for PCFsoft
                // vr = false                    // Switches renderer to VR mode, then uses gl.setAnimationLoop
                // gl2 = false                   // Enables webgl2
                // concurrent = false            // Enables React concurrent mode
                // resize = undefined            // Resize config, see react-use-measure's options
                // orthographic = false          // Creates an orthographic camera if true
                // noEvents = false              // Switch off raytracing and event support
                // pixelRatio = undefined        // You could provide window.devicePixelRatio if you like
                // invalidateFrameloop = false   // When true it only renders on changes, when false it's a game loop
                // updateDefaultCamera = true    // Adjusts default camera on size changes
                // onCreated                     // Callback when vdom is ready (you can block first render via promise)
                // onPointerMissed 
            >   
                <rectAreaLight
                    intensity={3}
                    position={[0, 10, -10]}
                    width={30}
                    height={30}
                    onUpdate={self => self.lookAt(new THREE.Vector3(0, 0, 0))}
                />

                <TextLoop  />
                {/* <Scene  mouse={mouseState} /> */}

            </Canvas> 
        </div>
    );
}

export default SmokeContainerReal;
