import React, { useCallback, useEffect, useRef, useState } from "react";
import * as THREE from 'three';
import {
    Canvas,
    extend as extendThree,
    useFrame,
    useLoader,
    useResource,
    useThree
} from "react-three-fiber";

// Import and register postprocessing classes as three-native-elements for react-three-fiber
// They'll be available as native elements <effectComposer /> from then on ...
import { EffectComposer } from './resources/postprocessing/EffectComposer';
import { RenderPass } from './resources/postprocessing/RenderPass';
import { GlitchPass } from './resources/postprocessing/GlitchPass';
extendThree({ EffectComposer, RenderPass, GlitchPass });

// import TextLoop from "../TextLoop";



const DEFAULT_LAYER = 0;
const OCCLUSION_LAYER = 1;











// The canvas houses this function, the scene. Scene is where the elements come together.
const ObjectMeshTetra = ({ layer = DEFAULT_LAYER }) => {
    const ref = useRef();
    // const [geometryRef, geometry] = useResource();
    // const [materialRef, material] = useResource();

    // useFrame(() => {
    //     ref.current.rotation.x = ref.current.rotation.y += 0.01
    // });
    useFrame(({ clock }) => {
        ref.current.position.x = Math.cos(clock.getElapsedTime()) * 1.1
        ref.current.rotation.x = ref.current.rotation.y += 0.001
        ref.current.rotation.x += 0.001
        ref.current.rotation.y += 0.005
        ref.current.rotation.z += 0.005
    });

    console.log('ref in objectMeshTet --> ', ref);

    return (
        <>
                <mesh 
                    ref={ref}
                    userData={{ test: 'hello' }} 
                    position={[ 0, 0, 2 ]} 
                    // rotation={[ 0, 0, 0 ]}
                    // scale={[ 2.0, 2.0, 2.0 ]}
                    receiveShadow
                    castShadow
                    visible 
                >
                    {/* <torusKnotBufferGeometry 
                        attach="geometry" 
                        args={[ 0.5, 0.15, 150, 32 ]} 
                    /> */}
                    <tetrahedronBufferGeometry 
                        attach="geometry"
                        radius={20}
                    />
                    <meshPhysicalMaterial 
                        attach="material" 
                        color="#0077ff"
                        roughness={1} 
                        clearcoat={1} 
                        clearcoatRoughness={0.2}
                        dithering 
                    />
                </mesh>
        </>
    )
}

const LightBar = () => {
    const lightBar = useRef();
    const { 
        size,
        // camera, 
    } = useThree();

    // const occlusionComposer = useRef()
    // const composer = useRef()

    // useEffect(() => {
    //     lightBar.current.setSize(size.width, size.height)
    //     // occlusionComposer.current.setSize(size.width, size.height)
    //     // composer.current.setSize(size.width, size.height)
    // }, [size])

    useFrame(() => {
        lightBar.current.rotation.z += 0.005;
    }, 1)


    return (
        <>
            <mesh 
                ref={lightBar} 
                // layers={OCCLUSION_LAYER} 
            >
                <boxBufferGeometry attach="geometry" args={[ 0.5, 20, 1 ]} />
                <meshBasicMaterial attach="material" color="lightblue" />
            </mesh>
        </>
    )
}


// // Creates a fullscreen colored plane
// const Plane = () => {
//     const { viewport } = useThree();
//     const plane = useRef();

//     return (
//         <mesh 
//             ref={plane}
//             scale={[ 100, 100, 1 ]}
//         >
//             <planeGeometry  attach="geometry"  args={[1, 1]} />
//             <meshPhysicalMaterial  attach="material"  color="#00ffff"  />
//         </mesh>
//     )
// }




// This is the canvas. It's the lowest level element in the three/webGl chain
const Canvasical = () => {
    // const {
    //     gl,                           // WebGL renderer 
    //     scene,                        // Default scene
    //     camera,                       // Default camera
    //     size,                         // Bounds of the view (which stretches 100% and auto-adjusts)
    // } = useThree();



    return (
        <>
            <Canvas
                className="container block__three-container" 
                style={{ backgroundColor: "#212121" }}
                // style={{ background: 'linear-gradient(45deg, #000f20 40%, #110020 80%, #030303 100%)' }}
                camera={{ 
                    fov: 75, 
                    position: [ 0, 0, 6 ],
                    zoom: 1,
                    focus: 10,
                    near: 10,
                    far: 200
                }} 
                shadowMap
                // children                      // Either a function child (which receives state) or regular children
                // gl                            // Props that go into the default webGL-renderer
                // camera                        // Props that go into the default camera
                // raycaster                     // Props that go into the default raycaster
                // shadowMap                     // Props that go into gl.shadowMap, can also be set true for PCFsoft
                // vr = false                    // Switches renderer to VR mode, then uses gl.setAnimationLoop
                // gl2 = false                   // Enables webgl2
                // concurrent = false            // Enables React concurrent mode
                // resize = undefined            // Resize config, see react-use-measure's options
                // orthographic = false          // Creates an orthographic camera if true
                // noEvents = false              // Switch off raytracing and event support
                // pixelRatio = undefined        // You could provide window.devicePixelRatio if you like
                // invalidateFrameloop = false   // When true it only renders on changes, when false it's a game loop
                // updateDefaultCamera = true    // Adjusts default camera on size changes
                // onCreated                     // Callback when vdom is ready (you can block first render via promise)
                // onPointerMissed 
            >
                {/* <Plane /> */}

                <ambientLight
                    color="#ffffff"
                    intensity={0.1}
                />
                <pointLight />
                <spotLight
                    color="#ffffff"
                    intensity={4}
                    distance={200}
                    // angle={1}
                    angle={Math.PI / 10}
                    penumbra={0}
                    decay={2}
                    position={[ 10, 20, 10 ]} 
                    castShadow
                    shadow-mapSize-width={2048} 
                    shadow-mapSize-height={2048}
                />


                <ObjectMeshTetra  />

                <LightBar />


            </Canvas> 

            {/* <TextLoop  /> */}

        </>
    );
}

export default Canvasical;
