import React, { useCallback, useEffect, useRef } from "react";
// import {
//     applyProps,
//     Canvas,
//     useRender,
//     useResource,
//     useThree
// } from "react-three-fiber";
import TextLoop from "../TextLoop";
// import * as resources from "./resources/index";

// applyProps(resources);

const Particle = ({ geometry, material }) => {
    let ref = useRef();
    let t = Math.random() * 100;
    let speed = 0.01 + Math.random() / 200;
    let factor = 20 + Math.random() * 100;
    let xFactor = -50 + Math.random() * 100;
    let yFactor = -50 + Math.random() * 100;
    let zFactor = -30 + Math.random() * 60;
    useRender(() => {
        t += speed;
        const s = Math.cos(t);
        ref.current.scale.set(s, s, s);
        ref.current.rotation.set(s * 5, s * 5, s * 5);
        ref.current.position.set(
            xFactor +
                Math.cos((t / 30) * factor) +
                (Math.sin(t * 1) * factor) / 10,
            yFactor +
                Math.sin((t / 20) * factor) +
                (Math.cos(t * 2) * factor) / 10,
            zFactor +
                Math.cos((t / 10) * factor) +
                (Math.sin(t * 3) * factor) / 20
        );
    });
    return <mesh ref={ref} material={material} geometry={geometry} />;
}

const Swarm = ({ mouse }) => {
    const light = useRef();
    const [geometryRef, geometry] = useResource();
    const [materialRef, material] = useResource();
    useRender(() =>
        light.current.position.set(
            mouse.current[0] / 20,
            -mouse.current[1] / 20,
            0
        )
    );
    return (
        <>
            <pointLight
                ref={light}
                distance={50}
                intensity={1.5}
                color="white"
            />
            <spotLight intensity={0.5} position={[10, 10, 40]} penumbra={1} />
            <mesh>
                <planeGeometry attach="geometry" args={[10000, 10000]} />
                <meshPhongMaterial
                    attach="material"
                    color="#575757"
                    depthTest={false}
                />
            </mesh>
            <dodecahedronBufferGeometry ref={geometryRef} args={[0.8, 0]} />
            <meshPhysicalMaterial ref={materialRef} />
            {geometry &&
                new Array(2000)
                    .fill()
                    .map((_, index) => (
                        <Particle
                            key={index}
                            material={material}
                            geometry={geometry}
                        />
                    ))}
        </>
    );
}

const Effect = () => {
    const composer = useRef();
    const { scene, gl, size, camera } = useThree();
    useEffect(() => void composer.current.setSize(size.width, size.height), [
        size
    ]);
    useRender(
        ({ gl }) => void ((gl.autoClear = true), composer.current.render()),
        true
    );
    return (
        <effectComposer ref={composer} args={[gl]}>
            <renderPass attachArray="passes" scene={scene} camera={camera} />
            <waterPass attachArray="passes" factor={2} />
            <afterimagePass attachArray="passes" factor={0.7} />
            <shaderPass
                attachArray="passes"
                args={[resources.FXAAShader]}
                material-uniforms-resolution-value={[
                    1 / size.width,
                    1 / size.height
                ]}
                renderToScreen
            />
        </effectComposer>
    );
}

const ThreeContainer = () => {


    const mouse = useRef([0, 0]);
    const onMouseMove = useCallback(
        ({ clientX: x, clientY: y }) =>
            (mouse.current = [
                x - window.innerWidth / 2,
                y - window.innerHeight / 2
            ]),
        []
    );
    return (
        <div className="block__three-container" onMouseMove={onMouseMove}>
            {/* 
            <Canvas camera={{ fov: 75, position: [0, 0, 50] }}>
                <Swarm mouse={mouse} />
                <Effect />
            </Canvas> 
            */}

            <TextLoop  />

        </div>
    );
}

export default ThreeContainer;
